import Vue from 'vue'
import VueRouter from 'vue-router'
import store from "@/store";

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: '后台管理',
    component: () => import(/* webpackChunkName: "Manage" */ '../views/Manage.vue'),
    redirect: "/manage",
    children: [
      {
        path: '/home',
        name: '主页',
        component: () => import(/* webpackChunkName: "Home" */ '../views/Home.vue'),
        children: [
          {
            path: '/personal',
            name: '个人信息',
            component: () => import(/* webpackChunkName: "personal" */ '../views/Personal.vue')
          },
        ]
      },
      {
        path: '/userManage',
        name: '用户管理',
        component: () => import(/* webpackChunkName: "userManage" */ '../views/UserManage.vue')
      },
      {
        path: '/productManage',
        name: '商品管理',
        component: () => import(/* webpackChunkName: "productManage" */ '../views/ProductManage.vue')
      },
      {
        path: '/categoryManage',
        name: '分类管理',
        component: () => import(/* webpackChunkName: "categoryManage" */ '../views/CategoryManage.vue')
      },
      {
        path: '/orderManage',
        name: '订单管理',
        component: () => import(/* webpackChunkName: "orderManage" */ '../views/OrderManage.vue')
      },
      {
        path: '/sale',
        name: '销售柱状图',
        component: () => import(/* webpackChunkName: "sale" */ '../views/Sale.vue')
      }
    ]
  },
  {
    path: '/login',
    name: '后台登录',
    // route level code-splitting
    // this generates a separate chunk (about.[hash].js) for this route
    // which is lazy-loaded when the route is visited.
    component: () => import(/* webpackChunkName: "login" */ '../views/Login.vue')
  },
]

const router = new VueRouter({
  routes
})

export const setRoutes = () => {
  const storeMenus = localStorage.getItem("menus");
  if (storeMenus){
    //拼装动态路由
    const mangeRoute =  {path: '/',name:'mange',component: ()=> import('../views/Manage.vue'),redirect:''}
  }
}

//路由守卫
router.beforeEach((to , from , next ) => {
  localStorage.setItem("currentPathName",to.name)
  store.commit("setPath")
  next()
})

export default router
