import { serverIp } from "../../public/config";
export default {
  name: "Personal",
  data() {
    return {
      serverIp: serverIp,
      form: {},
      admin: localStorage.getItem("admin") ? JSON.parse(localStorage.getItem("admin")) : {}
    };
  },
  created() {
    this.getAdmin().then(res => {
      console.log(res);
      this.form = res;
    });
  },
  methods: {
    async getAdmin() {
      return (await this.request.get("/admin/adminName/" + this.admin.adminName)).data;
    },
    save() {
      this.request.post("/admin", this.form).then(res => {
        if (res.status === 200) {
          this.$message.success("保存成功");

          // 触发父级更新admin的方法
          this.$emit("refreshAdmin");

          // 更新浏览器存储的用户信息
          this.getAdmin().then(res => {
            res.token = JSON.parse(localStorage.getItem("admin")).token;
            localStorage.setItem("admin", JSON.stringify(res));
          });
        } else {
          this.$message.error("保存失败");
        }
      });
    }
  }
};
