import { serverIp } from "../../public/config";
export default {
  name: "Personal",
  data() {
    return {
      serverIp: serverIp,
      form: {},
      admin: localStorage.getItem("admin") ? JSON.parse(localStorage.getItem("admin")) : {}
    };
  },
  created() {
    this.getAdmin().then(res => {
      console.log(res);
      this.form = res;
    });
  },
  methods: {
    async getAdmin() {
      return (await this.request.get("/admin/adminName/" + this.admin.adminName)).data;
    },
    edit() {
      this.request.put("/admin/editAdmin", this.form).then(res => {
        if (res) {
          this.$message.success("编辑成功");
          this.load();
          this.$emit('getAdmin');
        } else {
          this.$message.error("编辑失败");
        }
      });
    }
  }
};
